import { createState, lazy, onCleanup, Suspense } from "solid-js";
import { render } from "solid-js/web";
import AsyncChild from "./AsyncChild";

const LazyChild = lazy(() => import("./SimpleChild"));
const LazyAsyncChild = lazy(() => import("./AsyncChild"));

const Loader = () => {
  const [state, setState] = createState({ count: 0 }),
    interval = setInterval(() => setState("count", (c) => c + 1), 1000);
  onCleanup(() => clearInterval(interval));

  return <div>Loading... {state.count}</div>;
};

const App = () => {
  const startTime = Date.now();

  return (
    <Suspense fallback={<Loader />}>
      <AsyncChild start={startTime} />
      <AsyncChild start={startTime} />
      <AsyncChild start={startTime} />
      <AsyncChild start={startTime} />
      <LazyChild start={startTime} />
      <LazyAsyncChild start={startTime} />
    </Suspense>
  );
};

render(App, document.getElementById("app"));

import { createResource } from "solid-js";

function createDelay(): Promise<number> {
  return new Promise((resolve) =>
    setTimeout(() => resolve(Date.now()), Math.random() * 8000)
  );
}

const AsyncChild = ({ start }) => {
  const [state] = createResource(createDelay);
  return <div>Async loaded after {state() - start}ms</div>;
};

export default AsyncChild;

export default ({ start }) => (
  <div>Simple loaded after {Date.now() - start}ms</div>
);
